SELECT /* CsCustTelPrmsInfo.getPhoneAppointManagement */ CASE WHEN TCCTRI.PROC_STATE = 'C' THEN '완료' WHEN TCCTRI.PROC_STATE = 'R' THEN '예약' WHEN TCCTRI.PROC_STATE = 'I' THEN '임박' WHEN TCCTRI.PROC_STATE = 'D' THEN '지연' END PROC_STATE , TO_CHAR(TCCTRI.PRMS_DTM,'YYYY-MM-DD HH24:MI:SS') AS PRMS_DTM , FN_GET_CODENAME('CS013',NOTI_TM_CD) || '분전' AS NOTI_TM_CD , FN_GET_CODENAME('CS008',PRMS_METH_CD) AS PRMS_METH_CD , TCCTRI.CNSL_MEMO , EMB.LOGIN_ID AS MBR_ID , EMB.MBR_NM , CCCI.TEL_RGN_NO , CCCI.TEL_TXNO_NO , CCCI.TEL_END_NO , ( SELECT ORD_NO FROM CS_CCN_ORD_GOODS_INFO CCOGI WHERE (CCOGI.ORD_NO IS NOT NULL AND CCOGI.ORD_NO != '') AND CCOGI.CCN_NO = CCCI.CCN_NO
AND CCOGI.ORD_NO = #{ordNoCcnNoText}
LIMIT 1 ) , CCCI.CCN_NO , (SELECT USER_NM FROM ST_USER_BASE SUB WHERE SUB.USER_ID = CCCI.ACPTMN_ID) AS ACPTMN_NM , (SELECT SDC.DEPT_NM FROM ST_USER_BASE SUB LEFT OUTER JOIN ST_DEPT_CD SDC ON SUB.DEPT_CD = SDC.DEPT_CD WHERE SUB.USER_ID = CCCI.ACPTMN_ID) AS ACPTMN_DEPT , (SELECT USER_NM FROM ST_USER_BASE SUB WHERE SUB.USER_ID = CCCI.PROCMN_ID) AS PROCMN_NM , (SELECT SDC.DEPT_NM FROM ST_USER_BASE SUB LEFT OUTER JOIN ST_DEPT_CD SDC ON SUB.DEPT_CD = SDC.DEPT_CD WHERE SUB.USER_ID = CCCI.PROCMN_ID) AS PROCMN_DEPT , TO_CHAR(TCCTRI.AEMP_PROC_DTM,'YYYY-MM-DD HH24:MI:SS') AS AEMP_PROC_DTM FROM CS_CUST_CNSL_INFO CCCI , ET_MBR_BASE EMB , ( SELECT CCN_NO , CNSL_PROC_SEQ , TEL_PRMS_SEQ , PRMS_DTM , NOTI_TM_CD , PRMS_METH_CD , PRMS_STAT_CD , CNSL_MEMO , AEMP_ID , AEMP_CONF_DTM , AEMP_PROC_DTM , TEL_TRY_CNT , CASE WHEN PRMS_STAT_CD = '20' THEN 'C' WHEN PROC_STATE < -10 THEN 'R' WHEN PROC_STATE >= -10 AND PROC_STATE < 0 THEN 'I' WHEN PROC_STATE >= 0 THEN 'D' END PROC_STATE FROM ( SELECT * , ( DATE_PART('DAY', NOW() - PRMS_DTM) * 24 + DATE_PART('HOUR',NOW() - PRMS_DTM)) * 60 + DATE_PART('MINUTE', NOW() - PRMS_DTM) AS PROC_STATE FROM CS_CUST_TEL_PRMS_INFO ) AS TEMP ) TCCTRI WHERE CCCI.CCN_NO = TCCTRI.CCN_NO AND CCCI.MBR_NO = EMB.MBR_NO
AND TCCTRI.PRMS_DTM >= TO_TIMESTAMP(#{startDate}||'000000', 'YYYYMMDDHH24MISS') AND TCCTRI.PRMS_DTM <= TO_TIMESTAMP(#{endDate}||'235959', 'YYYYMMDDHH24MISS')
AND TCCTRI.AEMP_PROC_DTM >= TO_TIMESTAMP(#{startDate}||'000000', 'YYYYMMDDHH24MISS') AND TCCTRI.AEMP_PROC_DTM <= TO_TIMESTAMP(#{endDate}||'235959', 'YYYYMMDDHH24MISS')
AND PROC_STATE LIKE #{procState}
AND PRMS_METH_CD LIKE #{prmsMethCd}
AND EMB.LOGIN_ID LIKE #{mbrText}
AND EMB.MBR_NM LIKE #{mbrText}
AND CCCI.ACPTMN_ID LIKE #{userText}
AND CCCI.PROCMN_ID LIKE #{userText}
AND EXISTS (SELECT * FROM CS_CCN_ORD_GOODS_INFO CCOGI WHERE CCOGI.CCN_NO = CCCI.CCN_NO AND CCOGI.ORD_NO = #{ordNoCcnNoText} )
AND CCCI.CCN_NO LIKE #{ordNoCcnNoText}
ORDER BY TCCTRI.PRMS_DTM DESC , CCCI.ACPTMN_ID DESC
