SELECT /* etDeliRegnByZipCd.getRegnPostNoList */ T.ZIP_NO , COALESCE(T.CTP_NM,'') ||' '|| COALESCE(T.SIG_NM,'') ||' '|| COALESCE(T.EMD_NM,'') ||' '|| COALESCE(T.ROAD_NM,'') AS BASE_ADDR FROM ( SELECT SZN.* FROM mv_post SZN WHERE 1=1
AND CTP_NM = #{deliRegnGbNm}
AND EMD_NM LIKE #{searchWord}||'%'
) T WHERE NOT EXISTS ( SELECT 1 FROM ET_DELI_REGN_BY_ZIP_CD EDRBZC WHERE EDRBZC.ZIP_NO = T.ZIP_NO )
