		  DISP_CTG_NO
		, CONR_NO
		, CONR_TGT_NO
		, CONR_TGT_CD
		, CONR_CONT_NO
		, DISP_STR_DTM
		, DISP_END_DTM
		, DISP_SEQ
		, DISP_YN
		, CONT_TITLE_NM
		, LINK_URL_ADDR
		, MOV_FRME_CD
		, CONT_PATH_NM
		, CONT_FILE_NM
		, CONT_DESC
		, ALT_CONT
		, HTML_FILE_CONT
		, SYS_REG_ID
		, SYS_REG_DTM
		, SYS_MOD_ID
		, SYS_MOD_DTM
) VALUES (
		  #{dispCtgNo}
		, #{conrNo}
		, #{conrTgtNo}
		, #{conrTgtCd}
		, #{conrContNo}
		, TO_TIMESTAMP(#{dispStrDtm}, 'YYYY-MM-DD HH24:MI:SS')
		, TO_TIMESTAMP(#{dispEndDtm}, 'YYYY-MM-DD HH24:MI:SS')
		, #{dispSeq}
		, #{dispYn}
		, #{contTitleNm}
		, #{linkUrlAddr}
		, #{movFrmeCd}
		, #{contPathNm}
		, #{contFileNm}
		, #{contDesc}
		, #{altCont}
		, #{htmlFileCont}
		, #{sysRegId}
		, now()
		, #{sysModId}
		, now()
)


	SELECT NEXTVAL('PR_CONR_CONT_INFO_SQ01') AS conrContNo

