mapper.select:
SELECT   /* prConrSetInfo.checkPrDispConrInfo */
		COUNT(*)
FROM 	PR_DISP_CONR_INFO
WHERE 	DISP_CTG_NO = #{dispCtgNo}
AND 	CONR_NO = #{conrNo}

mapper.insert:
INSERT INTO PR_DISP_CONR_INFO (  /* PrConrSetInfo.prDispConrInfoInsert */
	  DISP_CTG_NO
	, CONR_NO
	, SYS_REG_ID
	, SYS_REG_DTM
	, SYS_MOD_ID
	, SYS_MOD_DTM
) VALUES (
	  #{dispCtgNo}
	, #{conrNo}
	, #{sysRegId}
	, now()
	, #{sysModId}
	, now()
)

mapper.select:
SELECT   /* prConrSetInfo.checkPrConrSetInfo */
COUNT(*)
FROM 	PR_CONR_SET_INFO
WHERE 	DISP_CTG_NO = #{dispCtgNo}
AND 	CONR_NO = #{conrNo}
AND 	CONR_TGT_NO = #{conrTgtNo}

mapper.insert:
INSERT INTO PR_CONR_SET_INFO (  /* prConrSetInfo.prConrSetInfoInsert_dpthNo2 */
		DISP_CTG_NO
		, CONR_NO
		, CONR_TGT_NO
		, DPTH_NO
		, DISP_SEQ
		, DISP_YN
		, GRP_TGT_NO
		, UPR_CONR_TGT_NO
		, SYS_REG_ID
		, SYS_REG_DTM
		, SYS_MOD_ID
		, SYS_MOD_DTM
) VALUES (
		  #{dispCtgNo}
		, #{conrNo}
		, #{conrTgtNo}
		, #{dpthNo}
		, #{dispSeq}
		, 'Y'
		, #{uprConrTgtNo}
		, #{uprConrTgtNo}
		, #{sysRegId}
		, now()
		, #{sysModId}
		, now()
)

INSERT INTO PR_CONR_SET_INFO (  /* prConrSetInfo.prConrSetInfoInsert_dpthNo1 */
		  DISP_CTG_NO
		, CONR_NO
		, CONR_TGT_NO
		, DPTH_NO
		, SET_NM
		, DISP_SEQ
		, DISP_YN
		, SET_DESC
		, GRP_TGT_NO
		, UPR_CONR_TGT_NO
		, SYS_REG_ID
		, SYS_REG_DTM
		, SYS_MOD_ID
		, SYS_MOD_DTM
) VALUES (
		  #{dispCtgNo}
		, #{conrNo}
		, #{conrTgtNo}
		, #{dpthNo}
		, #{setNm}
		, #{dispSeq}
		, #{dispYn}
		, #{setDesc}
		, #{grpTgtNo}
		, null
		, #{sysRegId}
		, now()
		, #{sysModId}
		, now()
)

mapper.update:
UPDATE 		PR_CONR_SET_INFO  /* prConrSetInfo.prConrSetInfoUpdate */
SET
		  DPTH_NO             = #{dpthNo}
		, SET_NM              = #{setNm}
		, DISP_SEQ            = #{dispSeq}
		, DISP_YN             = #{dispYn}
		, SET_DESC            = #{setDesc}
		, GRP_TGT_NO          = #{grpTgtNo}
		, UPR_CONR_TGT_NO     = null
		, SYS_MOD_ID          = #{sysModId}
		, SYS_MOD_DTM         = now()
WHERE 	DISP_CTG_NO = #{dispCtgNo}
AND 	CONR_NO = #{conrNo}
AND 	CONR_TGT_NO = #{conrTgtNo}
