     , SYS_MOD_DTM          = NOW()
WHERE STD_CTG_NO = #{stdCtgNo}

UPDATE PR_STD_CTG /* prStdCtgTrx.updateStandardCategoryGrid */
SET    STD_CTG_NM      = #{stdCtgNm}
     , USE_YN          = #{useYn}
     , SORT_SEQ        = #{sortSeq}
     , LEAF_CTG_YN     = #{leafCtgYn}
     , SYS_MOD_ID      = #{sysModId}
     , SYS_MOD_DTM     = NOW()
 WHERE STD_CTG_NO = #{stdCtgNo}

