mapper.sql:
SELECT IC.EVLT_ITEM_NO /* PrEvltItemCd.evltItemListForAdddBase */ , IC.EVLT_ITEM_NM , CASE WHEN IC.EVLT_ITEM_NO IN (
#{evltItemNo}
) THEN 'N' ELSE 'Y' END ADD_YN , IC.SYS_REG_ID , TO_CHAR(IC.SYS_REG_DTM,'YYYY-MM-DD HH24:MI:SS') AS SYS_REG_DTM , IC.SYS_MOD_ID , TO_CHAR(IC.SYS_MOD_DTM,'YYYY-MM-DD HH24:MI:SS') AS SYS_MOD_DTM FROM PR_EVLT_ITEM_CD IC WHERE IC.USE_YN = 'Y' AND IC.EVLT_ITEM_NM LIKE #{evltItemNm} || '%' ORDER BY EVLT_ITEM_NO

mapper.select:
SELECT /* PrEvltItemCd.checkDuplicatedEvltItemNm */
       CASE WHEN COUNT(*)  >  0 THEN TRUE
       ELSE FALSE END RESULT
  FROM PR_EVLT_ITEM_CD
 WHERE EVLT_ITEM_NM = #{evltItemNm}
   AND NOT EVLT_ITEM_NO = #{evltItemNo}
