mapper.insert:
INSERT INTO PR_PREST_HIST (/* PrPrestHistTrx.insertCoupon */
            GOODS_NO
          , APLY_STR_DT
          , APLY_END_DT
          , PREST_NM
          , USE_YN
          , SYS_REG_ID
          , SYS_REG_DTM
          , SYS_MOD_ID
          , SYS_MOD_DTM
     )
     VALUES (
            #{goodsNo}
          , REPLACE(#{aplyStrDt},'-','')
          , REPLACE(#{aplyEndDt},'-','')
          , #{prestNm}
          , #{useYn}
          , #{sysRegId}
          , NOW()
          , #{sysModId}
          , NOW()
     )

mapper.update:
UPDATE PR_PREST_HIST /* PrPrestHistTrx.updatePrPrestHist */
   SET PREST_NM = #{prestNm}
     , USE_YN = #{useYn}
     , SYS_MOD_ID = #{sysModId}
     , SYS_MOD_DTM = NOW()
 WHERE GOODS_NO = #{goodsNo}
   AND APLY_STR_DT = REPLACE(#{aplyStrDt},'-','')
   AND APLY_END_DT = REPLACE(#{aplyEndDt},'-','')

mapper.delete:
DELETE /* PrPrestHistTrx.deleteAllPrPrestHist */
  FROM PR_PREST_HIST
 WHERE GOODS_NO = #{goodsNo}
