mapper.insert:

    SELECT NEXTVAL('cc_noti_msg_info_sq01') AS notiMsgSeq

/* CcNotiMsgInfoTrxMapper.saveNtcMsgInfo */
INSERT INTO CC_NOTI_MSG_INFO
( NOTI_MSG_SEQ
    ,SITE_NO
    ,MSG_GB_CD
    ,APLY_STR_DT
    ,APLY_END_DT
    ,BASE_NOTI_METHOD_CD
    ,TITLE
    ,MSG
    ,USE_YN
    ,SYS_REG_ID
    ,SYS_REG_DTM
    ,SYS_MOD_ID
    ,SYS_MOD_DTM)
VALUES
( #{notiMsgSeq}
    ,#{siteNo}
    ,#{msgGbCd}
    ,replace(#{aplyStrDt}, '-', '')
    ,replace(#{aplyEndDt},'-', '')
    ,#{baseNotiMethodCd}
    ,#{title}
    ,#{msg}
    ,#{useYn}
    ,#{sysRegId}
    ,now()
    ,#{sysRegId}
    ,now()
)

mapper.update:
UPDATE CC_NOTI_MSG_INFO /* CcNotiMsgInfoTrxMapper.updateNtcMsgInfo */
    SET APLY_STR_DT   = replace(#{aplyStrDt}, '-', '')
    , APLY_END_DT   = replace(#{aplyEndDt}, '-', '')
    , BASE_NOTI_METHOD_CD = #{baseNotiMethodCd}
    , MSG_GB_CD     = #{msgGbCd}
    , USE_YN        = #{useYn}
    , TITLE         = #{title}
    , MSG           = #{msg}
    , SYS_MOD_ID 	= #{sysModId}
    , SYS_MOD_DTM 	= now()
WHERE NOTI_MSG_SEQ = #{notiMsgSeq}
