mapper.sql:
SELECT /* prFaqInfo.FaqList */ PFI.FAQ_NO , FN_GET_CODENAME( 'CM014', PFI.FAQ_LGRP_CD ) AS FAQ_LGRP_CD , FN_GET_CODENAME( 'CM015', PFI.FAQ_MGRP_CD ) AS FAQ_MGRP_CD , PFI.TITLE , PFI.BB_YN , PFI.SORT_SEQ , PFI.QRY_CNT , PFI.SYS_MOD_ID , TO_CHAR(PFI.SYS_MOD_DTM,'YYYY-MM-DD HH24:MI:SS') AS SYS_MOD_DTM , PFI.SYS_REG_ID , TO_CHAR(PFI.SYS_REG_DTM, 'YYYY-MM-DD HH24:MI:SS') AS SYS_REG_DTM FROM PR_FAQ_INFO PFI -- FAQ정보
AND PFI.FAQ_LGRP_CD = #{faqLgrpCd}
AND PFI.FAQ_MGRP_CD = #{faqMgrpCd}
AND PFI.TITLE LIKE '%' || #{title} || '%'
AND PFI.BB_YN LIKE '%' || #{bbYn} || '%'
ORDER BY SYS_REG_DTM DESC

mapper.select:
SELECT /* prFaqInfo.faqOverLapCheck */ COUNT(*) FROM PR_FAQ_INFO
FAQ_LGRP_CD = #{faqLgrpCd} AND FAQ_MGRP_CD = #{faqMgrpCd}
AND FAQ_NO <> #{faqNo} -- 업데이트시 체크할때는 본인꺼 제외하고 체크
