mapper.update:
UPDATE PR_FAQ_INFO    				/* prFaqInfo.updatePrFaqInfo */
SET 	  BB_YN          = #{bbYn}
		, SORT_SEQ       = #{sortSeq}
		, SYS_MOD_ID     = #{sysModId}
		, SYS_MOD_DTM     = NOW()
WHERE 	  FAQ_NO = #{faqNo}

mapper.insert:

	SELECT NEXTVAL('PR_FAQ_INFO_SQ01') AS faqNo

INSERT INTO PR_FAQ_INFO ( 	/* prFaqInfo.insertPopupPrFaqInfo */
