		, TITLE
		, PC_NTC_CONT
		, MO_NTC_CONT
		, SYS_REG_ID
		, SYS_REG_DTM
		, SYS_MOD_ID
		, SYS_MOD_DTM
) VALUES (
		  #{faqNo}
		, #{faqLgrpCd}
		, #{faqMgrpCd}
		, #{uprFixYn}
		, #{sortSeq}
		, #{bbYn}
		, #{title}
		, #{pcNtcCont}
		, #{moNtcCont}
		, #{sysRegId}
		, NOW()
		, #{sysModId}
		, NOW()
)

