mapper.sql:
SELECT /* stGrpCd.getStGrpCdListBase */ GRP_CD , GRP_CD_NM , GRP_CD_DESC , USE_YN , REF_1_VAL_DESC , REF_2_VAL_DESC , REF_3_VAL_DESC , REF_4_VAL_DESC , REF_5_VAL_DESC , SYS_REG_ID , TO_CHAR(SYS_REG_DTM, 'YYYY-MM-DD HH24:MI:SS') SYS_REG_DTM , SYS_MOD_ID , TO_CHAR(SYS_MOD_DTM, 'YYYY-MM-DD HH24:MI:SS') SYS_MOD_DTM FROM ST_GRP_CD WHERE 1 = 1 AND GRP_CD LIKE #{grpCd} || '%' AND GRP_CD_NM LIKE #{grpCdNm} || '%'
AND USE_YN = #{useYn}
ORDER BY SYS_MOD_DTM DESC

mapper.select:
SELECT COUNT(*) /* stGrpCd.getGrpCdCheck */
  FROM ST_GRP_CD
 WHERE GRP_CD = #{grpCd}

SELECT COUNT(*) /* stGrpCd.getGrpCdNmCheck */
  FROM ST_GRP_CD
 WHERE GRP_CD_NM = #{grpCdNm}
   AND GRP_CD  <>  #{grpCd}
