mapper.insert:
INSERT INTO ST_RECVMN_INFO(		/* stRecvmnInfo.insertRecvmn */
		RECV_GRP_NO
		, USER_ID
		, SYS_REG_ID
		, SYS_REG_DTM
		, SYS_MOD_ID
		, SYS_MOD_DTM
) VALUES (
	#{recvGrpNo}
	, #{userId}
    , #{sysRegId}
    , now()
    , #{sysRegId}
    , now()			
)

mapper.update:
DELETE /* stRecvmnInfo.deleteRecvmn */
   FROM ST_RECVMN_INFO
WHERE RECV_GRP_NO = #{recvGrpNo}
	AND USER_ID = #{userId}
