SELECT /* stRtTgtBase.subMenuList */ A.RT_TGT_SEQ , A.RT_TGT_NM , A.SYS_GB_CD , A.RT_TGT_TYP_CD , A.CALL_URL , A.BTN_ID , A.UPR_RT_TGT_SEQ , A.USE_YN , A.SORT_SEQ , A.POPUP_YN , A.CUST_INFO_INCL_YN , A.RMK_CONT FROM ST_RT_TGT_BASE A
WHERE A.UPR_RT_TGT_SEQ = #{rtTgtSeq}
ORDER BY SORT_SEQ
