mapper.update:
UPDATE 	  ST_USER_BASE /* stUserBaseTrx.updateChagePassword */
SET 	  PWD 		   = #{pwd}
	 	, LST_PWD_CHG_DTM = NOW()
	 	, PWD_CNTN_FAIL_CNT  = 0

	 	, PWD_INI_YN		= #{pwdIniYn}

	 	, SYS_MOD_ID 	    = #{userId}
	 	, SYS_MOD_DTM 	    = NOW()
 WHERE 	  USER_ID = #{userId}

UPDATE ST_USER_BASE /* stUserBaseTrx.updatePwdUnlock */
SET PWD_LOCK_YN = #{pwdLockYn}
, PWD_CNTN_FAIL_CNT = #{pwdCntnFailCnt}
WHERE USER_ID	= #{userId}
  AND PWD_LOCK_YN = 'Y'

UPDATE ST_USER_BASE /* stUserBaseTrx.updateInitPassword */
SET PWD = #{pwd}
, LST_PWD_CHG_DTM = NOW()
, PWD_LOCK_YN = #{pwdLockYn}
, PWD_CNTN_FAIL_CNT = #{pwdCntnFailCnt}
, PWD_INI_YN = #{pwdIniYn}
, SYS_MOD_ID = #{sysModId}
, SYS_MOD_DTM = NOW()
WHERE USER_ID = #{userId}

		UPDATE ST_USER_BASE /* stUserBaseTrx.updatePasswordByPasswordInitialize */
		   SET PWD 				  = #{pwd}
			 , LST_PWD_CHG_DTM	= now()
			 , PWD_CNTN_FAIL_CNT  = 0
 			 , PWD_INI_YN		 = 'N'
		     , SYS_MOD_ID = #{userId}
		     , SYS_MOD_DTM = NOW()
		 WHERE USER_ID = #{userId}
